mapper.update:
UPDATE asset
SET asset_type = #{assetType}
   ,feature_hw = #{featureHw}
   ,feature_sw = #{featureSw}
   ,interface_type = #{interfaceType}
   ,asset_performer = #{assetPerformer}
WHERE asset_num = #{assetNum}

mapper.select:
SELECT T1.*, T2.* FROM( SELECT asset_num as assetNum, asset_type as assetType, feature_hw as featureHw, feature_sw as featureSw, interface_type as interfaceType, asset_performer as assetPerformer, B.rnum as rnum FROM( SELECT ROWNUM AS rnum, A.* FROM( SELECT * FROM ASSET WHERE 1=1
)A WHERE ROWNUM <= (#{pageSize}*(#{pageNum}-1) + #{pageSize}) )B WHERE rnum >= (#{pageSize}*(#{pageNum}-1)+1) )T1 NATURAL JOIN ( SELECT COUNT(*) as totalCnt FROM ASSET WHERE 1=1
)T2
